import { useState } from "react";
import logo from './logo.svg';
import PostInput from "./PostInput";
import PostList from "./PostList";
import './App.css';

function App() {
  const [posts, setPosts] = useState([]);
  const [post, setPost] = useState("");

  const addPost = () => {
    if (post !== "") {
      setPosts([...posts, post]);
      setPost("");
    }
  };

  const deletePost = (text) => {
    const newPosts = posts.filter((post) => {
      return post !== text;
    });
    setPosts(newPosts);
  };

  return (
      <div className="App">        
        <h1>Blog Post App</h1>
        <PostInput post={post} setPost={setPost} addPost={addPost} />
        <PostList list={posts} remove={deletePost} />
    </div>
  );
};

export default App;
